import { configureStore } from "@reduxjs/toolkit";
import { createStore, useSlice } from "react-redux";

const initialProductCount = {count:0, isAdded:false, toggleCart:false};
const cartSlice = useSlice({
    name: 'productQuantity',
    initalState: initialProductCount,
    reducers:{
        addToCart(state){
            state.count++;
            state.isAdded=true;
        },
        increaseQuantity(state){
            state.count++;
        },
        decreaseQuantity(state){
            state.count--;
        },
        toggleCart(state){
            state.toggleCart = !state.toggleCart;
        },
}});

const store = configureStore({cart: cartSlice.reducer});

export const cartActions = cartSlice.actions;
export default store;
