import { createSlice } from "@reduxjs/toolkit";

const initialProductState = { item:[],
    totalQuantity: 0
};
const productSlice = createSlice({
    name: 'cart',
    initalState: initialProductState,
    reducers:{
        addToCart(state,action){
            const newItem = action.payload;
            const existingItem = state.items.find(item=>item.id===newItem.id);
            if(!existingItem){
                state.items.push({itemId: newItem.id, price: newItem.price, quantity:1, totalPrice: newItem.price , title: newItem.title});
            }
            else{
                existingItem.quantity++;
                totalPrice = totalprice+newItem;
            }
        },
        increaseQuantity(state){
            state.count++;
        },
        decreaseQuantity(state){
            state.count--;
        },
        removeEmptyProduct(state){
            state.isAdded = false;
        },
}});

export const productActions = productSlice.actions;
export default productSlice.reducer;
